import React, { useState } from 'react';
import FileUpload from './components/FileUpload';
import ProcessButton from './components/ProcessButton';
import './App.css';

function App() {
  const [files, setFiles] = useState([]);
  const [isProcessing, setIsProcessing] = useState(false);
  const [lastUsedLocation, setLastUsedLocation] = useState('');

  const handleUploadComplete = (uploadedFiles, location) => {
    const filesArray = Array.isArray(uploadedFiles) ? uploadedFiles : [uploadedFiles];
    setFiles(prev => [...prev, ...filesArray]);
    setLastUsedLocation(location); // Save the location for processing step
  };

  const handleProcess = async () => {
    setIsProcessing(true);

    try {
      if (!files.length) throw new Error('No files uploaded');
      if (!lastUsedLocation) throw new Error('Location is missing');

      const response = await fetch('http://localhost:5001/api/process', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ locationNumber: lastUsedLocation })
      });

      if (!response.ok) {
        const errorData = await response.json().catch(() => ({}));
        throw new Error(errorData.message || 'Processing failed');
      }

      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = `equipment_report_${lastUsedLocation || new Date().toISOString()}.csv`;
      document.body.appendChild(a);
      a.click();
      a.remove();
    } catch (error) {
      console.error('Processing error:', error);
      alert(`Error: ${error.message}`);
    } finally {
      setIsProcessing(false);
    }
  };

  return (
    <div className="app-container">
      <h1>Equipment Processor</h1>
      <FileUpload onUploadComplete={handleUploadComplete} />
      <ProcessButton 
        onClick={handleProcess} 
        disabled={!files.length || isProcessing}
        loading={isProcessing}
      />
    </div>
  );
}

export default App;
