import React, { useState } from 'react';
import FileUpload from './components/FileUpload';
import ProcessButton from './components/ProcessButton';
import './App.css';

function App() {
  const [files, setFiles] = useState([]); // Always initialize as array
  const [isProcessing, setIsProcessing] = useState(false);

  // Modified to handle both array and single file cases
    const handleUploadComplete = async (uploadedFiles) => {
      const filesArray = Array.isArray(uploadedFiles) ? uploadedFiles : [uploadedFiles];
      setFiles(prev => [...prev, ...filesArray]);

      try {
        const photoFormData = new FormData();
        const manufacturerFormData = new FormData();

        for (const file of filesArray) {
          if (file.type?.startsWith('image/') || ['image/jpeg', 'image/png', 'image/jpg'].includes(file.type)) {
            photoFormData.append('photos', file);
          } else if (file.name?.endsWith('.xlsx') || file.name?.endsWith('.xls')) {
            manufacturerFormData.append('file', file);
          }
        }

        if (photoFormData.has('photos')) {
          const photoResponse = await fetch('http://localhost:5001/api/upload/photos', {
            method: 'POST',
            body: photoFormData
          });
          if (!photoResponse.ok) throw new Error('Photo upload failed');
        }

        if (manufacturerFormData.has('file')) {
          const manufacturerResponse = await fetch(
            'http://localhost:5001/api/upload/manufacturer',
            {
              method: 'POST',
              body: manufacturerFormData
            }
          );
          if (!manufacturerResponse.ok) throw new Error('Manufacturer file upload failed');
        }
      } catch (error) {
        console.error('Upload error:', error);
        alert(`Upload failed: ${error.message}`);
      }
    };


const handleProcess = async () => {
  setIsProcessing(true);

  try {
    if (!files?.length) throw new Error('No files uploaded');
    if (!location) throw new Error('Location is missing');

    const processResponse = await fetch('http://localhost:5001/api/process', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ locationNumber: location })
    });

    if (!processResponse.ok) {
      const errorData = await processResponse.json().catch(() => ({}));
      throw new Error(errorData.message || 'Processing failed');
    }

    const blob = await processResponse.blob();
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `equipment_report_${location || new Date().toISOString()}.csv`;
    document.body.appendChild(a);
    a.click();
    a.remove();

  } catch (error) {
    console.error('Processing error:', error);
    alert(`Error: ${error.message}`);
  } finally {
    setIsProcessing(false);
  }
};


    return (
      <div className="app-container">
        <h1>Equipment Processor</h1>
        <FileUpload onUploadComplete={handleUploadComplete} />
        <ProcessButton 
          onClick={handleProcess} 
          disabled={!files?.length || isProcessing}
          loading={isProcessing}
        />
      </div>
    );

}

export default App;
