import React, { useState } from 'react';
import FileUpload from './components/FileUpload';
import LocationInput from './components/LocationInput';
import ProcessButton from './components/ProcessButton';
import './App.css';

function App() {
  const [files, setFiles] = useState([]);
  const [location, setLocation] = useState('');
  const [isProcessing, setIsProcessing] = useState(false);

  const handleUpload = (uploadedFiles) => {
    setFiles(uploadedFiles);
  };

  const handleProcess = async () => {
    setIsProcessing(true);

    try {
      // Upload files
      const formData = new FormData();
      files.forEach(file => {
        formData.append('files', file);
      });

      const uploadResponse = await fetch('http://localhost:5001/api/upload', {
        method: 'POST',
        body: formData
      });

      if (!uploadResponse.ok) throw new Error('Upload failed');

      // Process data
      const processResponse = await fetch('http://localhost:5001/api/process', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ locationNumber: location })
      });

      if (!processResponse.ok) throw new Error('Processing failed');

      // Download result
      const blob = await processResponse.blob();
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = 'equipment_report.csv';
      document.body.appendChild(a);
      a.click();
      a.remove();

    } catch (error) {
      console.error('Error:', error);
      alert(error.message);
    } finally {
      setIsProcessing(false);
    }
  };

  return (
    <div className="app-container">
      <h1>Equipment Processor</h1>
      <FileUpload onUpload={handleUpload} />
      <LocationInput value={location} onChange={setLocation} />
      <ProcessButton 
        onClick={handleProcess} 
        disabled={!files.length || !location || isProcessing}
        loading={isProcessing}
      />
    </div>
  );
}

export default App;
