import React, { useState } from 'react';
import FileUpload from '../components/JHAFileUpload';

const JHA = () => {
  const [uploadedFiles, setUploadedFiles] = useState([]);
  const [location, setLocation] = useState('');

  const handleUploadComplete = (files, uploadedLocation) => {
    setUploadedFiles([...uploadedFiles, ...Object.values(files).flat()]);
    setLocation(uploadedLocation);
    alert('JHA PDF and Manufacturer Excel file uploaded successfully!');
  };

  return (
    <div className="jha-page-container">
      <h1>JHA Document Upload</h1>
      <p>Please upload the Job Hazard Analysis PDF files and corresponding manufacturer Excel file.</p>

      <FileUpload
        onUploadComplete={handleUploadComplete}
        location={location}
        setLocation={setLocation}
      />

      <div className="uploaded-summary">
        {uploadedFiles.length > 0 && (
          <>
            <h3>Uploaded Files:</h3>
            <ul>
              {uploadedFiles.map((file, index) => (
                <li key={index}>{file.name}</li>
              ))}
            </ul>
          </>
        )}
      </div>
    </div>
  );
};

export default JHA;
