import React, { useState } from 'react';
import FileUpload from './components/FileUpload';
import ProcessButton from './components/ProcessButton';
import './App.css';

function App() {
  const [files, setFiles] = useState([]);
  const [location, setLocation] = useState('');
  const [isProcessing, setIsProcessing] = useState(false);

  const handleUploadComplete = (uploadedFiles, uploadedLocation) => {
    const filesArray = Array.isArray(uploadedFiles) ? uploadedFiles : [uploadedFiles];
    setFiles(prev => [...prev, ...filesArray]);
    setLocation(uploadedLocation); // Save location from FileUpload
  };

const handleProcess = async () => {
  setIsProcessing(true);

  try {
    if (!files.length) throw new Error('No files uploaded');
    if (!location) throw new Error('Location is missing');

    // First verify backend connection
    const healthCheck = await fetch('http://localhost:5001');
    if (!healthCheck.ok) throw new Error('Backend not responding');

    const response = await fetch('http://localhost:5001/api/process', {
      method: 'POST',
      headers: { 
        'Content-Type': 'application/json',
        'Accept': 'application/json'
      },
      body: JSON.stringify({ locationNumber: location })
    });

    if (!response.ok) {
      const errorText = await response.text();
      throw new Error(errorText || 'Request failed');
    }

    // ... rest of your success handling code
  } catch (error) {
    console.error('Connection error:', error);
    alert(`Backend connection failed. Please ensure:
    1. Backend server is running
    2. No firewall blocking port 5001
    3. Try refreshing the page

    Error details: ${error.message}`);
  } finally {
    setIsProcessing(false);
  }
};

  return (
    <div className="app-container">
      <h1>Equipment Processor</h1>
      <FileUpload onUploadComplete={handleUploadComplete} 
                  location={location}
                  setLocation={setLocation}/>
      <ProcessButton 
        onClick={handleProcess} 
        disabled={!files.length || !location || isProcessing}
        loading={isProcessing}
      />
    </div>
  );
}

export default App;
